sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
			var rightnow = new GlideDateTime();
			var overdueRecord = new GlideRecord('x_58872_needit_needit_task');
			overdueRecord.addQuery('due_date','<=',rightnow);
			overdueRecord.addQuery('state','<',3);
			overdueRecord.addQuery('assigned_to',current.assigned_to);
			overdueRecord.query();

// 			var assignedto = current.assigned_to;
// 			var count = overdueRecord.getRowCount();

			template.print("you have more " + (overdueRecord.getRowCount() - 1) + "record overdue");


})(current, template, email, email_action, event);
